body {
  margin: 0;
  height: 100vh;
  display: flex;
  background: radial-gradient(ellipse at bottom, #01010f 0%, #000 100%);
  justify-content: center;
  align-items: center;
  font-family: sans-serif;
  overflow: hidden;
}

.neon-sphere {
  width: 200px;
  height: 200px;
  border-radius: 50%;
  background: radial-gradient(circle at 30% 30%, #00ffff 5%, #002b36 60%, #000 100%);
  box-shadow:
    0 0 20px #0ff,
    0 0 40px #0ff,
    0 0 60px #0ff,
    inset 0 0 30px #0ff;

  position: relative;
}

.neon-sphere::after {
  content: "";
  position: absolute;
  top: 15%;
  left: 15%;
  width: 70%;
  height: 70%;
  background: radial-gradient(circle, rgba(255, 255, 255, 0.3), transparent 70%);
  border-radius: 50%;
  filter: blur(8px);
  animation: shimmer 6s ease-in-out infinite;
}

@keyframes pulse {
  0%, 100% {
    transform: scale(1);
    box-shadow:
      0 0 20px #0ff,
      0 0 40px #0ff,
      0 0 60px #0ff,
      inset 0 0 30px #0ff;
  }
  50% {
    transform: scale(1.05);
    box-shadow:
      0 0 30px #0ff,
      0 0 60px #0ff,
      0 0 90px #0ff,
      inset 0 0 40px #0ff;
  }
}

@keyframes shimmer {
  0%, 100% {
    transform: translate(0, 0);
  }
  50% {
    transform: translate(10px, 10px);
  }
}


