bool HasDuplicates_Naive(List<string> trackingNumbers)
{
    int n = trackingNumbers.Count;

    for (int i = 0; i < n; i++)
    {
        for (int j = i + 1; j < n; j++)
        {
            if (trackingNumbers[i] == trackingNumbers[j])
                return true; // duplicate found
        }
    }
    return false; // no duplicates
}


