package Sorting;

public class Example {
    public static void main(String[] args) {

    int[] a = {50,40,30,20,10}; 
    for(int i=0; i<a.length-1;i++)
    {
        if(a[i]>a[i+1])
        {
        int temp = a[i]; 
        a[i] = a[i+1]; 

        a[i+1] = temp; 
        }
    }
    for(int i=0; i<a.length; i++)
        System.out.print(a[i] + " "); 

    System.out.println("Maximum " + a[a.length-1]);
      }
}


