Why?
Accept a pattern
Read a file or stdin
Print lines that contain the pattern
No regex engine. No flags. 
#include <stdio.h>      // Allows us to read files and print to the screen
#include <string.h>     // Gives us string functions like strstr()

#define MAX_LINE 1024   // Max characters weâ€™ll read per line

int main(int argc, char *argv[]) {
    if (argc < 2) {
        fprintf(stderr, "Usage: %s pattern [file]\n", argv[0]);
        return 1;
    }

    char *pattern = argv[1];
    FILE *fp = (argc > 2) ? fopen(argv[2], "r") : stdin;

    if (!fp) {
        perror("fopen");
        return 1;
    }

    char line[MAX_LINE];
    while (fgets(line, sizeof(line), fp)) {
        if (strstr(line, pattern)) {
            fputs(line, stdout);
        }
    }

    if (fp != stdin) fclose(fp);
    return 0;
}

