{
    FL_BSTIM16_InitTypeDef Bstim16;
    FL_NVIC_ConfigTypeDef nvic;

//-------------------------------------------   
    Bstim16.prescaler = 8000 - 1;//8M/8 = 1M                    /* é¢„åˆ†é¢‘ç³»æ•° */
    Bstim16.autoReload = 1000 - 1;  //1/1M*1000 =1ms        /* è‡ªåŠ¨é‡è£…è½½å€¼ */
    Bstim16.autoReloadState = FL_ENABLE;                    /* é¢„è£…è½½ä½¿èƒ½ */
    Bstim16.clockSource = FL_CMU_BSTIM16_CLK_SOURCE_APBCLK; /* æ—¶é’Ÿæº */

