package Array;

public class MaxValue {
public static void main(String[] args) {
    int[] marks = {88,73, 82,88,95}; 
    int max  = 0; 
    for(int i=0; i<marks.length; i++)
    {
        if(marks[i]>max)    
            max = marks[i]; 
    }
    System.out.println(max);
}
}

