entry.summary:
I ported own example app from Gitlab by Gnome.
In C# 
csnamespace DeafMan1983.Examples;
using static DeafMan1983.Examples.ExampleAppSharp;

using static DeafMan1983.Interop.Gtk4.Gio;

using static DeafMan1983.Utilities.UtilitiesForSByte;

class MainSharp // like main.c from Getting Started in Gtk-4 and Glib-2.0
{
    static int Main(string[] args)
    {
        int argc = args.Length;
        unsafe
        {
            sbyte** argv = StringArrayToSByteDoublePointer(args);
            return g_application_run(G_APPLICATION(example_app_new()), argc, argv);
        }
    }
}

